import React from 'react';
import '../css/Git.css'; // Import your Git CSS file here

function Git() {

    const navigate = useNavigate();
    const [showBackButton, setShowBackButton] = useState(false);
    let lastScrollY = window.scrollY;

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY < lastScrollY) {
                setShowBackButton(true); // Show button when scrolling up
            } else {
                setShowBackButton(false); // Hide button when scrolling down
            }
            lastScrollY = window.scrollY;
        };

        window.addEventListener('scroll', handleScroll, { passive: true });

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleBack = () => {
        navigate(-1); // Go back to the previous page
    };

    return (
        <div className="git-page">
            <section className="git-intro">
                <h1>Git Tutorial</h1>
                <p>Welcome to the Git tutorial. Learn the basics of Git version control and how it can improve your coding workflow.</p>
            </section>

            <section className="git-basics">
                <h2>Git Basics</h2>
                <p>Understand the fundamentals of Git, including what it is and why it's used by developers worldwide.</p>
                {/* Include more detailed content or sub-sections here */}
            </section>

            <section className="git-commands">
                <h2>Common Git Commands</h2>
                <p>Learn about common Git commands like git commit, git push, git pull, and how to use them.</p>
                {/* Include more detailed content or sub-sections here */}
            </section>

            <section className="git-branching">
                <h2>Branching and Merging</h2>
                <p>Explore advanced topics such as branching and merging, and how they facilitate collaborative development.</p>
                {/* Include more detailed content or sub-sections here */}
            </section>

            {/* Add more sections as needed */}
        </div>
    );
}

export default Git;
