import React, { useState, useEffect } from "react";
import image1 from '../img/1.jpeg';
import image2 from '../img/2.jpeg';
import image3 from '../img/3.jpeg';

function Login({ onLogin }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [currentImage, setCurrentImage] = useState(0);
    const images = [image1, image2, image3];

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImage((currentImage) => (currentImage + 1) % images.length);
        }, 3000); // Change image every 3 seconds

        return () => clearInterval(interval);
    }, [images.length]); // Add images.length to the dependency array


    const handleSubmit = (event) => {
        event.preventDefault();
        onLogin(email, password); // Call onLogin prop with email and password
        //print
    };

    return (
        <div className="login-form">
            <div className="title-container">

            <div className="introduction">
                    <h2>Welcome to AI/ML HUB</h2>
                    <p>Log in for free courses</p>
                </div>

                <div className="slideshow-container">
                    {images.map((img, index) => (
                        <img 
                            key={index} 
                            src={img} 
                            alt={`Slide ${index}`}
                            style={{ opacity: index === currentImage ? 1 : 0 }}
                        />
                    ))}
                </div>

            </div>
            <div className="form-container"> {/* Add this wrapper */}

            <h2>Welcome to AI/ML HUB</h2>
                <p>Log in for free courses</p>
                <form onSubmit={handleSubmit}>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="submit">Login</button>
                </form>
                <a href="/forgot-password">Forgot Password?</a>
                <a href="/sign-up">Sign Up</a>
            </div>
        </div>
    );
}

export default Login;
