import React from 'react';
import { useState }from "react";
import { Link } from 'react-router-dom';
import '../css/Nav.css';

// function Navbar() {
//     return (
        // <div className='navBar'>
        //     <div className='Home'>
        //         <Link to="/">Home</Link>
        //     </div>
        //     <div className='AboutUs'>
        //         <Link to="/about">About Us</Link>
        //     </div>
        //     <div className='ContactUs'>
        //         <Link to="/contact">Contact Us</Link>
        //     </div>
        // </div>

function Navbar({ isLoggedIn, onLogout, onHomeClick}) {
    const [showAiDropdown, setShowAiDropdown] = useState(false);
    const [showCodingDropdown, setShowCodingDropdown] = useState(false);


    const handleHomeClick = () => {
        if (isLoggedIn) {
            onHomeClick();
        } else {
            alert("Please login first!");
        }
    };

    return (
        <nav className="navbar">
            <div className="logo">MyAppLogo</div>
            <div className="nav-links">
                <div className="nav-item" onClick={handleHomeClick}>
                    <Link to="/">HOME</Link>
                </div>
                <div className="nav-item"
                     onMouseEnter={() => setShowAiDropdown(true)} 
                     onMouseLeave={() => setShowAiDropdown(false)}>
                    COURSES
                    {showAiDropdown && (
                        <div className="dropdown-content">
                            {/* Dropdown links */}
                        </div>
                    )}
                </div>
                <div className="nav-item"
                     onMouseEnter={() => setShowCodingDropdown(true)} 
                     onMouseLeave={() => setShowCodingDropdown(false)}>
                    CODING
                    {showCodingDropdown && (
                        <div className="dropdown-content">
                            {/* Dropdown links */}
                        </div>
                    )}
                </div>
                <div className="nav-item" onClick={handleHomeClick}>
                    <Link to="/about-us">ABOUT US</Link>
                </div>
                {!isLoggedIn && 
                    <div className="nav-item">
                        <Link to="/sign-up">SIGN UP</Link>
                    </div>
                }
                {isLoggedIn && 
                    <div className="nav-item" onClick={onLogout}>
                        LOGOUT
                    </div>
                }
            </div>
        </nav>
    );
}

export default Navbar;
